    public String setTaxData(IssueRequest request) {

        String result = "";
        Integer procVal = 0;
        String msgVal = "";

        logger.info("Setting Tax Data");


        ContractorData contractorData = request.getContractorData();
        logger.info("contractorData: {}", contractorData);
        String taxSysCode = contractorData.getTaxSystemCode().orElse("");
        String sTaxSystemCode = (contractorData.getTaxSystemCode() == null || contractorData.getTaxSystemCode().equals("")) ? "0" : taxSysCode;
//        String sTaxZipCode = (contractorData.getTaxZipCode() == null ) ? "" : contractorData.getTaxZipCode();
        String sTaxZipCode = contractorData.getTaxZipCode().orElse("");
        
        String physMca = contractorData.getPersonType();
        
        String middleName = contractorData.getContractingMiddleName().orElse("");
        String regimen = contractorData.getCorporateRegime().orElse("");

        String dataInfo =  physMca.equals("S")? middleName: regimen;
        logger.info("dataInfo: {}", dataInfo);

        Date currentDate= new Date();
        
        String name = request.getContractorData().getContractingFistName().orElse("");
        
        String lastName =  request.getContractorData().getContractingLastName().orElse("");
        String rfc = request.getContractorData().getDocumCode().orElse("XAXX010101000");
        String address1 = request.getContractorData().getAddressName1().orElse("");
        String address2 = request.getContractorData().getAddressName2().orElse("");
        String address3 = request.getContractorData().getAddressName3().orElse("");
        String postalCode = request.getContractorData().getPostalCode().orElse("");
        String stateCode = request.getContractorData().getStateCode().orElse("");
        String provinceCode = request.getContractorData().getProvinceCode().orElse("");
        String countryCode = request.getContractorData().getCountryCode().orElse("");
        String telephone = request.getContractorData().getTelephoneNumber().orElse("");

        Map<String, String> mapRes = iThirdRepository.cargaDatos(P_COD_CIA
                , "RFC"
                , rfc
                , contractorData.getPersonType()
                , Integer.valueOf(sTaxSystemCode)
                , name
                , dataInfo
                , lastName
                , address1
                , address2
                , ""
                , ""
                , sTaxZipCode
                , Integer.valueOf(contractorData.getProvinceCode().orElse("0"))
                , Integer.valueOf(contractorData.getStateCode().orElse("0"))
                , countryCode
                , ""
                , currentDate
                , procVal
                , msgVal);

        logger.info("|procval - msgVal| fron cargaDatos: |{} - {}|", procVal, msgVal);

        return msgVal;

    }


    public String getEmision(Map<String, String> mapPoliza, List<Map<String, String>> listaDatosVariables, List<Map<String, String>> listaDatosVariablesCoverage, List<Map<String, String>> listCoberturas, Map<String, String> mapContratante, Map<String, String> mapAsegurado, List<Map<String, String>> listaBeneficiarios, Map<String, String> mapDatosBancarios, String strCotizacion) {
        String strResultado = "";
        try {
            Map<String, Object> drEmision =this.setEmision(mapPoliza, listaDatosVariables, listaDatosVariablesCoverage, listCoberturas, mapContratante, mapAsegurado, listaBeneficiarios, mapDatosBancarios,  strCotizacion);

            if(drEmision.get("pnum_poliza") != null){
                strResultado= String.valueOf(drEmision.get("pnum_poliza"));
            }else{
                strResultado = "No se pudo realizar la Emisi贸n de su P贸liza, faltan datos: " + drEmision.get("ptxt_error").toString();
                throw new Exception(strResultado);
            }
        }catch(Exception e){
            logger.info("No se pudo realizar la Emisi贸n de su P贸liza " + e.getMessage());
        }
        return strResultado;
    }

